mapper.update:
UPDATE mubs_resource_grid
creator = #{creator}, createDate = #{createDate}, lastModifier = #{lastModifier}, lastModDate = #{lastModDate}, status = #{status}, resId = #{resId}, displayName = #{displayName}, colId = #{colId}, orderNum = #{orderNum}, width = #{width}, editoptions = #{editoptions}, edittype = #{edittype}, unformat = #{unformat}, description = #{description}, moduleName = #{moduleName},
WHERE id = #{id}

