        AND status = #{status}


        AND resId = #{resId}


        AND displayName = #{displayName}


        AND colId = #{colId}


        AND orderNum = #{orderNum}


        AND width = #{width}


        AND editoptions = #{editoptions}


        AND edittype = #{edittype}


        AND unformat = #{unformat}


        AND description = #{description}


        AND moduleName = #{moduleName}

